import React from 'react';
import axios from 'axios';

function UploadDoc(props) {
    const [doc, setDoc] = React.useState();
    const cloudName = 'striveworks-test'
    const apiKey = '149715514944462'
    const uploadPreset = 'netqgt9d'
    const url = `https://api.cloudinary.com/v1_1/${cloudName}/image/upload`;

    const docSelected = event => {
        setDoc(event.target.files[0])
    }

    const uploadDoc = () => {
        const fd = new FormData();
        fd.append("api_key", apiKey);
        fd.append('file', doc);
        fd.append("public_id", doc.name)
        fd.append('upload_preset', uploadPreset)
        // axios.post(url, fd)
        //     .then(res => {
        //         console.log(res)
        //     })
        //     .catch((err) => {
        //         console.log(err);
        //     });
        props.imageUploaded(doc)
    }

    return (
        <div>
            <input type="file" className="docs_header" name="Upload" onChange={docSelected}/>
        </div>
    );
}

export default UploadDoc;
