import React from 'react';
import axios from 'axios';

function UploadDoc(props) {
    // Should be handled in proper backend but left for demo
    const CLOUD_NAME = 'striveworks-test';
    const API_KEY = '149715514944462';
    const UPLOAD_PRESET = 'netqgt9d'
    const URL = `https://api.cloudinary.com/v1_1/${CLOUD_NAME}/image/upload`;

    const uploadDoc = (doc) => {
        const fd = new FormData();
        fd.append("api_key", API_KEY);
        fd.append('file', doc);
        fd.append("public_id", doc.name)
        fd.append('upload_preset', UPLOAD_PRESET)
        // axios.post(URL, fd)
        //     .then(res => {
        //         console.log(res)
        //     })
        //     .catch((err) => {
        //         console.log(err);
        //     });
        props.imageUploaded(doc)
    }

    return (
        <div>
            <input type="file" className="docs_header" onChange={(event) => uploadDoc(event.target.files[0])}/>
        </div>
    );
}

export default UploadDoc;
